`,(0,t.jsxDEV)(o.pre,{children:(0,t.jsxDEV)(o.code,{className:"language-jsx",render:"true",children:`function DialogCustomSize() {
  return (
    <DialogRoot>
      <DialogTrigger asChild>
        <button
          type="button"
          style={{
            border: '1px solid #475569',
            borderRadius: 5,
            padding: 10,
          }}
        >
          Open Dialog
        </button>
      </DialogTrigger>
      <DialogPortal>
        <DialogBackdrop preventCloseOnClick />
        <DialogContent>
          <DialogClose asChild>
            <CloseIconButton />
          </DialogClose>
          <DialogBody />
          <DialogFooter />
        </DialogContent>
      </DialogPortal>
    </DialogRoot>
  );
}

render(<DialogCustomSize />);

function CloseIconButton(props) {
  return (
    <button
      {...props}
      type="button"
      style={{
        position: 'absolute',
        top: 0,
        right: 0,
        padding: '0.875rem',
        border: 'none',
        outline: 'none',

        cursor: 'pointer',
        boxSizing: 'border-box',
        color: 'inherit',
        background: 'transparent',
        fontSize: '0.875rem',
        lineHeight: 1,
        verticalAlign: 'middle',
        display: 'inline-flex',
        justifyContent: 'center',
        alignItems: 'center',
      }}
    >
      <svg
        width="1em"
        height="1em"
        viewBox="0 0 24 24"
        fill="currentColor"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"
          fill="currentColor"
        />
      </svg>
    </button>
  );
}

function DialogBody() {
  return <div style={{ padding: '2rem' }}>Dialog Content</div>;
}

function DialogFooter() {
  return (
    <div style={{ padding: '1rem 1.5rem', textAlign: 'right' }}>
      <DialogClose asChild>
        <button
          type="button"
          style={{
            backgroundColor: 'transparent',
            marginRight: 3,
          }}
        >
          Close
        </button>
      </DialogClose>
    </div>
  );
}
