import {
  DrawerBackdrop,
  DrawerClose,
  DrawerContent,
  DrawerPortal,
  DrawerRoot,
  DrawerTrigger,
} from '@melio-ui/drawer';

import './styles.css';

export default function DrawerDemo() {
  return (
    <DrawerRoot>
      <DrawerTrigger asChild>
        <button type="button" className="drawer-trigger">
          Open Drawer
        </button>
      </DrawerTrigger>
      <DrawerPortal>
        <DrawerBackdrop className="drawer-backdrop" />
        <DrawerContent className="drawer-content">
          <div className="drawer-header">Title</div>
          <DrawerClose asChild>
            <button type="button" className="drawer-close-button">
              <CloseIcon />
            </button>
          </DrawerClose>
          <div className="drawer-body">Drawer Content</div>
          <div className="drawer-footer">
            <DrawerClose asChild>
              <button
                type="button"
                style={{
                  backgroundColor: 'transparent',
                  marginRight: 3,
                  padding: 5,
                }}
              >
                Cancel
              </button>
            </DrawerClose>
            <button
              type="button"
              style={{
                color: '#fff',
