`,(0,o.jsxDEV)(r.pre,{children:(0,o.jsxDEV)(r.code,{className:"language-jsx",render:"true",children:`function DrawerPlacement() {
  return (
    <DrawerRoot placement="left">
      <DrawerTrigger asChild>
        <button
          type="button"
          style={{
            border: '1px solid #2e68da',
            padding: '5px 10px',
            borderRadius: 5,
          }}
        >
          Left
        </button>
      </DrawerTrigger>
      <DrawerPortal>
        <DrawerBackdrop />
        <DrawerContent>
          <Title>Title</Title>
          <DrawerClose asChild>
            <CloseButton />
          </DrawerClose>
        </DrawerContent>
      </DrawerPortal>
    </DrawerRoot>
  );
}

render(<DrawerPlacement />);

function Title(props) {
  const { children } = props;
  return (
    <div
      style={{ padding: '1rem 1.5rem', borderBottom: '0.0625rem solid #cccccc', flex: '0 1 auto' }}
    >
      {children}
    </div>
  );
}

function CloseButton(props) {
  return (
    <button
      {...props}
      style={{
        position: 'absolute',
        right: 0,
        top: 0,
        padding: '0.875rem',
        cursor: 'pointer',
      }}
    >
      <svg
        width="1em"
        height="1em"
        viewBox="0 0 24 24"
        fill="currentColor"
        xmlns="http://www.w3.org/2000/svg"
      >
        <path
          d="M19 6.41L17.59 5 12 10.59 6.41 5 5 6.41 10.59 12 5 17.59 6.41 19 12 13.41 17.59 19 19 17.59 13.41 12z"
          fill="currentColor"
        />
      </svg>
    </button>
  );
}
