  display: flex;
  width: 100%;
  color: #e2e8f0;
}

.number-input-field {
  display: inline-block;
  width: 100%;
  min-width: 0;
  position: relative;
  border: 0.0625rem solid rgba(255, 255, 255, 0.35);
  box-sizing: border-box;
  background-color: rgb(9, 50, 122);
  color: inherit;
  padding: 0.3125rem 0.6875rem;
  line-height: 1;
  font-size: 1rem;
  height: 2rem;
}
.number-input-field:focus {
  outline: none;
  border-color: #208aff;
  border-width: 0.0625rem;
  box-shadow: 0 0 0.125rem #208aff;
}
.number-input-field:disabled {
  background-color: rgba(255, 255, 255, 0.08);
  color: rgba(255, 255, 255, 0.2);
  border-color: rgba(255, 255, 255, 0.2);
  cursor: not-allowed;
}

.number-input-button-group {
  height: 2rem;
}

.number-input-button {
  display: block;
  width: 100%;
  height: 1rem;
  padding: 0;

  cursor: pointer;
  box-sizing: border-box;
  color: inherit;
  background: transparent;
  border: 0.0625rem solid rgba(255, 255, 255, 0.35);
  font-size: 0.875rem;
  line-height: 1;
}
.number-input-button:disabled {
  cursor: not-allowed;
  background-color: rgba(255, 255, 255, 0.08);
  color: rgba(255, 255, 255, 0.2);
  border-color: rgba(255, 255, 255, 0.2);
}
