  display: inline-block;
  cursor: pointer;
  vertical-align: middle;
  position: relative;
}
.switch-root[data-readonly] {
  cursor: default;
  pointer-events: none;
}
.switch-root[data-disabled] {
  opacity: 0.5;
  cursor: default;
}
.switch-root > input[type='checkbox'] {
  cursor: inherit;
  position: absolute;
  opacity: 0;
  width: 0.0625rem;
  height: 0.0625rem;
  margin: 0;
  padding: 0;
  z-index: 1;
}

.switch-label {
  padding-left: 0.3125rem;
  padding-right: 0.3125rem;
}

.switch-track {
  display: inline-flex;
  position: relative;
  box-sizing: content-box;
  height: 1.625rem;
  border-radius: 0.9375rem;
  background-color: rgba(255, 255, 255, 0.24);
  padding: 0.125rem;
  min-width: 3rem;
  align-items: center;
  cursor: pointer;
  vertical-align: middle;
  transition: background-color 500ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
}
.switch-track[data-state='checked'] {
  background-color: #208aff;
}
.switch-root[data-disabled] .switch-track {
  cursor: not-allowed;
}

.switch-thumb {
  background-color: white;
  width: 1.375rem;
  height: 1.375rem;
  border-radius: 6.25rem;
  box-shadow:
    0 2px 1px -1px rgb(0 0 0 / 20%),
    0 1px 1px 0 rgb(0 0 0 / 14%),
    0 1px 3px 0 rgb(0 0 0 / 12%);
  position: absolute;
  top: 0.25rem;
  left: 0.25rem;
  transition: all 0.2s ease-in-out;
}
.switch-thumb[data-state='checked'] {
  left: calc(100% - 1.625rem);
}
