  display: flex;
  flex-direction: column;
  min-width: 3.125rem;
  margin: 0;
  border-bottom: none;
  border-right: 1px solid rgba(255, 255, 255, 0.35);
}

.tabs-tab {
  display: inline-flex;
  align-items: center;
  border: 0.0625rem solid transparent;
  border-bottom: none;
  bottom: -0.1875rem;
