  data={[
    {
      attribute: '[data-orientation]',
      values: '"horizontal" | "vertical"',
    },
  ]}
/>

## Examples

### defaultChecked

```jsx
<CheckboxRoot defaultChecked>
  <CheckboxIndicator>
    <CheckboxIcon />
  </CheckboxIndicator>
  <CheckboxLabel>defaultChecked</CheckboxLabel>
</CheckboxRoot>
```

### disabled

```jsx
<CheckboxRoot disabled>
  <CheckboxIndicator>
    <CheckboxIcon />
  </CheckboxIndicator>
  <CheckboxLabel>disabled</CheckboxLabel>
</CheckboxRoot>
```

### indeterminate

```jsx
<CheckboxRoot indeterminate>
  <CheckboxIndicator>
    <CheckboxIcon />
  </CheckboxIndicator>
  <CheckboxLabel>indeterminate</CheckboxLabel>
</CheckboxRoot>
```

### Indicator children function

```jsx render=true
function IndicatorFunc() {
  return (
    <CheckboxRoot>
      <CheckboxIndicator>
        {(checked: boolean) => {
          if (checked) return <CheckedUserIcon />;
          return <UncheckedUserIcon />;
        }}
      </CheckboxIndicator>
      <CheckboxLabel>User</CheckboxLabel>
    </CheckboxRoot>
  );
}

render(<IndicatorFunc />);

