  opacity: 1;
  position: fixed;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  background-color: rgba(0, 0, 0, 0.5);
  z-index: 1200;
  animation: fadein 0.3s;
}

.drawer-content {
  position: fixed;
  margin: 0 auto;
  width: 32.5rem;
  min-width: 12.5rem;
  min-height: 2.625rem;
  z-index: 1200;
  border-radius: 0.375rem;
  animation: fadein 0.3s;

