  top: 0;
  left: 0;
  width: 100%;
  height: 40%;
}
.drawer-content[data-placement='bottom'] {
  bottom: 0;
  left: 0;
  width: 100%;
  height: 40%;
}
.drawer-content[data-placement='right'] {
  top: 0;
  right: 0;
  width: 40%;
  height: 100%;
}
.drawer-content[data-placement='left'] {
  top: 0;
  left: 0;
  width: 40%;
  height: 100%;
}

