  display: inline-block;
  cursor: pointer;
  position: relative;
  width: 18.75rem;
  padding: 0.4375rem 0;
}
.slider-root[data-disabled] {
  opacity: 0.3;
  cursor: default;
  pointer-events: none;
}
.slider-root[data-orientation='vertical'] {
  width: 0.25rem;
  height: 100%;
  padding: 0 0.8125rem;
}

.slider-track {
  width: 100%;
  height: 0.25rem;
  position: absolute;
  border-radius: 0.0625rem;
  background-color: rgba(255, 255, 255, 0.24);
}
.slider-root[data-orientation='vertical'] .slider-track {
  width: 0.25rem;
  height: 100%;
}

.slider-range {
  left: 0;
  position: absolute;
  background-color: #208aff;
  height: inherit;
}
.slider-root[data-orientation='vertical'] .slider-range {
  width: 100%;
}

.slider-thumb {
  display: flex;
  width: 0.875rem;
  height: 0.875rem;
  position: absolute;
  border-radius: 50%;
  background-color: #208aff;
  outline: 0;
  margin-top: -0.375rem;
  margin-left: -0.4375rem;
  align-items: center;
  justify-content: center;
}
.slider-root[data-orientation='vertical'] .slider-thumb {
  margin-left: -0.3438rem;
  margin-bottom: -0.4375rem;
}

.slider-label {
  z-index: 1;
  white-space: nowrap;
  font-weight: 500;
  font-size: 0.875rem;
  line-height: 1.43;
  letter-spacing: 0.01071em;
  transition: transform 150ms cubic-bezier(0.4, 0, 0.2, 1) 0ms;
  top: -0.625rem;
  transform-origin: center bottom;
  transform: translateY(-100%) scale(1);
  position: absolute;
  background-color: #272c36;
  border-radius: 0.125rem;
  color: #ffffff;
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 0.25rem 0.75rem;
}
.slider-label::before {
  position: absolute;
  content: '';
  width: 0.5rem;
  height: 0.5rem;
  bottom: 0;
  left: 50%;
  transform: translate(-50%, 50%) rotate(45deg);
  background-color: inherit;
}
